package syntax.day_01;

public class VarArea {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int v1 = 100;
		int v2 = v1+1000;
		if (v1 < 100) {
			System.out.println("작다");
			System.out.println(v2);
		} else {
			System.out.println(v2);
			System.out.println("작지않다");
		}
		
		
	}

}
